import java.util.Scanner;public class VasilijeInCacak {public static void main(String[] args) {Scanner in = new Scanner(System.in);int t = in.nextInt(); while (t-- > 0) {long n = in.nextLong(); long k = in.nextLong(); long x = in.nextLong();long min = k*(k+1)/2;long total = n*(n+1)/2;long max = total - ((n-k) * (n-k+1)/2);if (x >= min && x <= max) {System.out.println("Yes");continue;}System.out.println("No");}}}
